import NavBar from '../../components/navbar/navbar'
import Layout, { siteTitle } from '../../components/layout/layout'
import {posts} from '../../posts/posts.js'


export default function Project(props) {
    return ( 
        <div>
            <NavBar selected='PROJECTS'/>
            <Layout>
                <section>
                    <h1>hola</h1>
                </section>
            </Layout>
        </div> 
    )
}

export async function getStaticPaths() {
    const paths = getAllPostsPaths()
    return {
        paths,
        fallback: false
    }
}
export async function getStaticProps({ params }) {
    dataProject = getDataProjectFromPath( params.path_title )
    return {
        props: {
            dataProject
        }
    }
}

function getAllPostsPaths() {
    return posts.map( aPost => {
        return {
            params: {
                path_title: aPost.title.replace(' ', '_').toLowerCase()
            }
        }
    })
}

function titleToPath( aTitle ) {
    return aTitle.replace(' ', '_').toLowerCase()
}


function pathIsPost( aPath , aPost) {
    return aPost.title.toLowerCase() == aPath.replace('_', ' ')
}

function getDataProjectFromPath( aPath ){
    postData = posts.filter( 
        (aPost) => { return pathIsPost(aPath, aPost) }
    )
    return postData
}
