import styles from './home.module.css'
import MotionAnimation from '../motion_animation/motion_animation'
import WebAnimation from '../web_animation/web_animation'
import { motion } from 'framer-motion';
import { getWindowWidth, getWindowHeight } from '../../window/window_dimensions.js';
import { useState, useEffect } from "react";
import dynamic from 'next/dynamic'
import ParallaxMousemove from 'react-parallax-mousemove'

const Frame = dynamic( () => import('framer').then((mod) => mod.Frame), { ssr: false } );

const easing = [0.6, -0.05, 0.01, 0.99];

const animationName = {
  initial: {
    x: -60,
    opacity: 0
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 0.9,
      ease: easing,
      delay: 0,
    },
  },
  exit: { 
    x: 0,
    opacity: 0
  }
}

const animationMotion = {
  initial: {
    x: -60,
    opacity: 0
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 0.9,
      ease: easing,
      delay: 0.2,
    },
  },
  exit: { 
    x: 0,
    opacity: 0
  }
}

const animationWeb = {
  initial: {
    x: -60,
    opacity: 0
  },
  animate: {
    x: 0,
    opacity: 1,
    transition: {
      duration: 0.9,
      ease: easing,
      delay: 0.4,
    },
  },
  exit: { 
    x: 0,
    opacity: 0
  }
}

const animationBG = {
  initial: {
    scale: 1.2,
    opacity: 0
  },
  animate: {
    scale: 1,
    opacity: 1,
    transition: {
      duration: 0.1,
      ease: easing
    }
  },
  exit: { 
    scale: 0.95,
    opacity: 0
  }
}

const stagger = {
  animate: {
    transition: {
      staggerChildren: 0.1
    }
  }
}

export default function Home({ children, home }) {

  const [ssrDone, setSsrDone ] = useState(false);

  useEffect(() => {
      setSsrDone(true)
      }, [])

  const widthBg = getWindowHeight() * ( 7680/4320 )
  const heightBg = getWindowHeight()

  const style = {
      outter: {
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          width:'100%',
          position: 'relative',
          overflow: 'hidden',
          marginLeft: 'auto',
          marginRigth: 'auto'
        },
        infoLayerStyle: {
          position: 'relative',
        },
        bgLayerStyle: {
          position: 'absolute',
          height: '100%',
        }
    }

    if(ssrDone) {
      return(
          <div>

            <div>

                <div 
                  className={styles.contaner_fix_pos}
                >

                  <Frame 
                    className={styles.home}
                  >
                    <motion.div
                      variants={stagger}
                      animate='animate'
                    >
                      <motion.div 
                      exit='exit'
                      initial='initial'
                      animate='animate'
                      variants={animationName}
                      className={styles.title}
                      >
                          <Frame className={styles.title_regular}>I,m </Frame>
                          <Frame className={styles.title_bold}>Franco</Frame>
                          <Frame className={styles.title_bold}>Gonzalez</Frame>
                          <Frame className={styles.title_regular}> .</Frame>
                      </motion.div>

                      <motion.div
                        exit='exit'
                        initial='initial'
                        animate='animate'
                        variants={animationMotion}
                      >
                        <MotionAnimation/>
                      </motion.div>

                      <motion.div
                        exit='exit'
                        initial='initial'
                        animate='animate'
                        variants={animationWeb}
                      >
                        <WebAnimation/>
                      </motion.div>

                    </motion.div>
                  </Frame>
                </div>

              </div>

              <ParallaxMousemove containerStyle={style.outter} fullHeight={true}>

                  <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
                      xFactor: -0.03,
                      yFactor: 0.03,
                      springSettings: {
                          stiffness: 150,
                          damping: 50
                      }
                  }}>
                      <Frame
                          height={ heightBg } 
                          width={ widthBg } 
                          top={0} 
                          left={ (getWindowWidth() - widthBg) / 2 } 
                          image='/background_parallax/shadow.png'
                          style={{
                              backgroundColor: 'rgba(0, 0, 0, 0)',
                              mixBlendMode: "multiply"
                          }}
                      />
                  </ParallaxMousemove.Layer>

                  <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
                      xFactor: 0.05,
                      yFactor: -0.03,
                      springSettings: {
                          stiffness: 150,
                          damping: 50
                      }
                      }}>

                      <Frame
                          height={getWindowHeight()} 
                          width={ getWindowHeight() * ( 7680/4320 ) } 
                          top={0}
                          left={ (getWindowWidth() - widthBg) / 2 } 
                          image='/background_parallax/layer_3.png'
                          style={{ 
                              backgroundColor: 'rgba(0, 0, 0, 0)'
                          }}
                      />

                  </ParallaxMousemove.Layer>

                  <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
                      xFactor: 0.10,
                      yFactor: -0.06,
                      springSettings: {
                          stiffness: 150,
                          damping: 50
                      }
                  }}>

                      <Frame
                          height={getWindowHeight()} 
                          width={ getWindowHeight() * ( 7680/4320 ) } 
                          top={0} 
                          left={ (getWindowWidth() - widthBg) / 2 }  
                          image='/background_parallax/layer_2.png'
                          style={{ 
                              backgroundColor: 'rgba(0, 0, 0, 0)'
                          }}
                      />

                  </ParallaxMousemove.Layer>

                  <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
                      xFactor: 0.15,
                      yFactor: -0.09,
                      springSettings: {
                          stiffness: 150,
                          damping: 50
                      }
                  }}>

                      <Frame
                          height={getWindowHeight()} 
                          width={ getWindowHeight() * ( 7680/4320 ) } 
                          top={0}  
                          left={ (getWindowWidth() - widthBg) / 2 } 
                          image='/background_parallax/layer_1.png'
                          style={{ 
                              backgroundColor:'rgba(0, 0, 0, 0)'
                          }}
                      />

                  </ParallaxMousemove.Layer>

              </ParallaxMousemove> 
          </div>
      )
  } 
  return (<div>Loading...</div>)



  }
