import styles from './animations.module.css';
import {useState} from 'react';

export default function MotionAnimation({ children, home }) {

    const [ playAnimation, setPlayAnimation ] = useState(false);

    return (
        <div onClick={ () => setPlayAnimation(true) } className={ playAnimation? styles.motion_animation_play : (styles.motion_animation && setPlayAnimation(false)) }></div>
    )
  }
