import styles from './animations.module.css';
import {useState} from 'react';

export default function MotionAnimation({ children, home }) {

    const [ playAnimation, setPlayAnimation ] = useState(false);
    const [ playAgainAnimation, setPlayAgainAnimation ] = useState(false);

    function playAnimationOneTime() {
        setPlayAnimation(true);
        console.log(playAnimation);
        setPlayAgainAnimation(!playAgainAnimation);
        console.log(playAgainAnimation);

    }

    function getStyle() {
        if(playAnimation){
            return getStyleAgain();
        } else {
            return styles.motion_animation
        }
    }

    function getStyleAgain() {
        if(playAgainAnimation){
            return styles.motion_animation_play;
        } else {
            return styles.motion_animation_play_again;
        }
    }

    return (
        <div 

        onClick={ () => playAnimationOneTime() } 
        className={ playAnimation? getStyleAgain() : styles.motion_animation }

        ></div>
    )
  }
