const easing = [0.6, -0.05, 0.01, 0.99];

function itemAnimation(aIndex) {
    return (
        {
            initial: {
                y: 60,
                opacity: 0,
            },
            animate: {
                y: 0,
                opacity: 1,
                transition: {
                    duration: 0.9,
                    ease: easing,
                    delay: aIndex * 0.1,
                },
            },
            exit: {
                y: -60,
                opacity: 0,
            },
        }
    );
} 

function imgItemAnimation(aIndex) {
    return (
        {
            initial: {
                scale: 1.3,
                opacity: 0,
            },
            animate: {
                scale: 1,
                opacity: 1,
                transition: {
                    duration: 0.9,
                    ease: easing,
                    delay: (aIndex * 0.1 + 0.1),
                },
            },
            exit: {
                scale: 1.3,
                opacity: 0,
            },
        }
    );
} 

export {itemAnimation, imgItemAnimation};
