import NavBar from '../../components/navbar/navbar'
import Layout, { siteTitle } from '../../components/layout/layout'
import {posts} from '../posts/posts.js'


export default function Project() {
    return ( 
        <div>
            <NavBar selected='PROJECTS'/>
            <Layout>
                <section>
                    <h1>{postData.title}</h1>
                    <h1>{postData.type}</h1>
                    <h1>{postData.thumbnail}</h1>
                </section>
            </Layout>
        </div> 
    )
}

export async function getStaticPaths() {
    const paths = getAllPostsTitles()
    return {
        paths,
        fallback: false
    }
}

export async function getStaticProps({ params }) {
    postData = getPostFromPath(params.path)
    return {
        props: {
            postData
        }
    }
}

function getAllPostsTitles() {
    return posts.map( aPost => {
        return {
                params: {
                    id: titleToPath( posts.title )
                }
            }
    })
}

function titleToPath( aTitle ) {
    return aTitle.replace(' ', '_').toLowerCase()
}

function pathToTitle( aPath ) {
    return aPath.replace('_', ' ')
}

function getPostFromPath( aPath ){
    postData = posts.filter( (x) => {
        return x.title.toLowerCase() == pathToTitle( aPath );
    })
}
