import styles from './background.module.css'
import ParallaxMousemove from 'react-parallax-mousemove'

export default function Background() {

    const style = {
        outter: {
            background: 'radial-gradient(50% 150%, #6CD7E8 50%, #59C2D3 100%)',
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            width:'100%',
            position: 'relative',
            overflow: 'hidden'
          },
          inner: {
            width:700,
            height:500,
            position: 'absolute',
            margin:'auto',
            top:0,
            left:0,
            right:0,
            bottom:0,
            textAlign:'center'
          },
          header: {
            fontFamily: 'Open Sans Condensed',
            textTransform: 'uppercase',
            color: 'white',
            textShadow: '0px 0px 2px #43AABA',
            fontSize: '50px',
            marginTop: '35px',
            fontWeight: 600
          },
          infoLayerStyle: {
            position: 'relative',

          },
          bgLayerStyle: {
            position: 'absolute',
            height: '100%',
          }
      }

      return (
        <div>
        <ParallaxMousemove containerStyle={style.outter} fullHeight={true}>

          <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
              xFactor: 0.05,
              yFactor: 0,
              springSettings: {
                stiffness: 50,
                damping: 30
              }
            }}>
            <img className={styles.img_style} src="/background_parallax/shadow.png" alt="Parallax Layer"></img>
          </ParallaxMousemove.Layer>

          <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
              xFactor: 0.15,
              yFactor: 0,
              springSettings: {
                stiffness: 50,
                damping: 30
              }
            }}>
            <img className={styles.img_style} src='/imgs/bg5.png' alt="Parallax Layer"></img>
          </ParallaxMousemove.Layer>

          <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
              xFactor: 0.25,
              yFactor: 0.025,
              springSettings: {
                stiffness: 50,
                damping: 30
              }
            }}>
            <img className={styles.img_style} src='/imgs/bg7.png' alt="Parallax Layer"></img>
          </ParallaxMousemove.Layer>

          <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
              xFactor: 0.4,
              yFactor: 0.05,
              springSettings: {
                stiffness: 50,
                damping: 30
              }
            }}>
            <img className={styles.img_style} src='/imgs/bg6.png' alt="Parallax Layer"></img>
          </ParallaxMousemove.Layer>

          <div style={style.inner}>

            <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                xFactor: 0.2,
                yFactor: 0,
                springSettings: {
                  stiffness: 50,
                  damping: 30
                }
              }}>
              <img className={styles.img_style} src="/background_parallax/layer_3.png" alt="Parallax Layer"></img>
            </ParallaxMousemove.Layer>

            <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                xFactor: 0.3,
                yFactor: 0,
                springSettings: {
                  stiffness: 50,
                  damping: 30
                }
              }}>
              <img  src='/imgs/bg2.png' alt="Parallax Layer"></img>
            </ParallaxMousemove.Layer>

            <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                xFactor: 0.4,
                yFactor: 0,
                springSettings: {
                  stiffness: 50,
                  damping: 30
                }
              }}>
              <img  src='/imgs/bg3.png' alt="Parallax Layer"></img>
            </ParallaxMousemove.Layer>

          </div>
        </ParallaxMousemove>
      </div>
      )

}
