import styles from './background.module.css'
import ParallaxMousemove from 'react-parallax-mousemove'

export default function Background() {

    const style = {
        outter: {
          background: 'radial-gradient(50% 150%, #6CD7E8 50%, #59C2D3 100%)',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          width:'100%',
          position: 'relative',
          overflow: 'hidden'
        },
        inner: {
          width:700,
          height:500,
          position: 'absolute',
          margin:'auto',
          top:0,
          left:0,
          right:0,
          bottom:0,
          textAlign:'center'
        },
        header: {
          fontFamily: 'Open Sans Condensed',
          textTransform: 'uppercase',
          color: 'white',
          textShadow: '0px 0px 2px #43AABA',
          fontSize: '50px',
          marginTop: '35px',
          fontWeight: 600
        },
        paragraph: {
          fontFamily: 'Roboto',
          fontSize: '20px',
          color: 'white',
          letterSpacing: '0.62px',
          lineHeight: '30px',
          fontWeight: 300
        },
        button: {
          fontFamily: 'Roboto',
          borderRadius: '100px',
          background: '#247B8A',
          textDecoration: 'none',
          color: 'white',
          textAlign: 'center',
          padding: '20px',
          marginTop: '40px',
          fontWeight: 100,
          display: 'block'
        },
        infoLayerStyle: {
          position: 'relative',
          marginTop: '-60px'
        },
        bgLayerStyle: {
          position: 'absolute',
          height: '100%',
          transform: 'translate(-15%, 17%)',
        }
      }

      return (
        <div>
          <ParallaxMousemove containerStyle={style.outter} fullHeight={true}>
            <ParallaxMousemove.Layer layerStyle={style.bgLayerStyle} config={{
                xFactor: -0.03,
                yFactor: 0.03,
                springSettings: {
                  stiffness: 50,
                  damping: 30
                }
              }}>

              <div className={styles.img_style}>
                <img src="/background_parallax/shadow.png" alt="Parallax Layer BG"></img>
              </div>

            </ParallaxMousemove.Layer>

            <div className={styles.img_style} style={style.inner}>

              <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                  xFactor: 0.03,
                  yFactor: -0.03,
                  springSettings: {
                    stiffness: 50,
                    damping: 30
                  }
                }}>

                <div className={styles.img_style}>
                    <img className={styles.img_style} src="/background_parallax/layer_1.png" alt="Parallax Layer 1"></img>
                </div>

              </ParallaxMousemove.Layer>

              <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                  xFactor: 0.06,
                  yFactor: -0.03,
                  springSettings: {
                    stiffness: 50,
                    damping: 30
                  }
                }}>

                <div className={styles.img_style}>
                    <img className={styles.img_style} src="/background_parallax/layer_2.png" alt="Parallax Layer 2"></img>
                </div>
              </ParallaxMousemove.Layer>

              <ParallaxMousemove.Layer layerStyle={style.infoLayerStyle} config={{
                  xFactor: 0.09,
                  yFactor: -0.03,
                  springSettings: {
                    stiffness: 50,
                    damping: 30
                  }
                }}>

                <div className={styles.img_style}>
                    <img className={styles.img_style} src="/background_parallax/layer_3.png" alt="Parallax Layer 3"></img>
                </div>

              </ParallaxMousemove.Layer>

            </div>

          </ParallaxMousemove>
        </div>
      )

}
