import styles from './animations.module.css';
import {useState} from 'react';
import {Transition, animate} from 'react-spring';

export default function MotionAnimation({ children, home }) {

    const [ playAnimation, setPlayAnimation ] = useState(false);

    function toggle() { 
        console.log("Se clickeo");
        setPlayAnimation(!playAnimation);
    }

    return (
        <Transition
        native
        items={this.state.playAnimation}
        from={backgroundPosition= '0 0px'}
        enter={backgroundPosition= '0 -3600px'}
        leave={backgroundPosition= '0 0px'}


        >
            <div onClick={toggle()} className={styles.motion_animation}></div>
        </Transition>
    )
  }
