// import { Frame, Scroll } from "framer";
import { getWindowWidth, getWindowHeight } from '../../window/window_dimensions.js';
import { useState, useEffect } from "react";
import dynamic from 'next/dynamic'
// import Color from 'framer'

const Frame = dynamic( () => import('framer').then((mod) => mod.Frame), { ssr: false } );
const Scroll = dynamic( () => import('framer').then((mod) => mod.Scroll), { ssr: false } );
const color = dynamic( () => import('framer').then((mod) => mod.Color), { ssr: false } );


export default function BgScroll() {
    const [ssrDone, setSsrDone ] = useState(false);

    useEffect(() => {
        setSsrDone(true)
        }, [])

    if(ssrDone) {
        return(
            <div>
                <h1> width={getWindowWidth()} height={getWindowHeight()} </h1>
                <Frame
                    height={getWindowHeight()} 
                    width={getWindowWidth()} 
                    top={0}  
                    image='/background_parallax/bg.png'
                />
                <Frame
                    height={getWindowHeight()} 
                    width={getWindowWidth()} 
                    top={0}  
                    image='/background_parallax/layer_3.png'
                />
                <Frame
                    height={getWindowHeight()} 
                    width={getWindowWidth()} 
                    top={0}  
                    image='/background_parallax/layer_2.png'
                />
                <Frame
                    height={getWindowHeight()} 
                    width={getWindowWidth()} 
                    top={0}  
                    image='/background_parallax/layer_1.png'
                    background={
                        color({r: 255, g: 255, b: 255, a:0})
                    }
                />

            </div>
        )
    } 
    return (<div>Loading...</div>)
}

