import styles from './projects_items.module.css'
import dynamic from 'next/dynamic'
//import { useMotionValue } from 'framer'

const Stack = dynamic(
    () => import('framer').then((mod) => mod.Stack),
    { ssr: false }
  )
const Frame = dynamic(
  () => import('framer').then((mod) => mod.Frame),
  { ssr: false }
)

const useMotionValue = dynamic(() =>
  import('framer').then(mod => mod.useMotionValue(0)), { ssr: false }
)

// TODO: Pasar a otro componente Window
const hasWindow = typeof window !== 'undefined';

function getWindowHeight() {
  const width = hasWindow ? window.innerWidth : null;
  const height = hasWindow ? window.innerHeight : null;
  return height
}

function getWindowWidth() {
  const width = hasWindow ? window.innerWidth : null;
  const height = hasWindow ? window.innerHeight : null;
  return width;
}
// end Window

const items = [{name:'Name project 1', type: "Type project 1"}
                , {name:'Name project 2', type: "Type project 2"}
                , {name:'Name project 3', type: "Type project 3"}
                , {name:'Name project 4', type: "Type project 4"}
                , {name:'Name project 5', type: "Type project 5"}];

export default function ProjectsItems() {
    const position = useMotionValue(0);
    var sliderPos = position;

    return ( 
      <div> 

        <Stack 
          size={100} 
          direction="horizontal" 
          alignment="start" gap='40' 
          distribution="start"  
          x={sliderPos}
        >

                {items.map(aItem => (

                <Frame key={aItem.name} backgroundColor='#180233' height={'50vh'} width={'40vh'} className={styles.project_item}>


                  <div className={styles.project_item_text}>
                    <h3>{aItem.name}</h3>
                    <h4>{aItem.type}</h4>
                  </div>

                  <img src='/Min_projects.png' alt='min' className={styles.img_min}></img>

                </Frame>

                ))}

        </Stack>

        <Frame 
          name={'Rail'}
          width={ Math.min( getWindowWidth(), 1920 ) - 400 }
          height={'1vh'}
          radius={0}
          position={"relative"}
          left={'0px'}
          background={"rgba(255,255,255,.1)"}
          top={'55vh'}
        >
          <Frame
            name={'Knob'}
            width={'8%'}
            height={'1vh'}
            radius={0}
            position={"relative"}
            left={'0%'}
            background={"rgba(255,255,255,.1)"}
            drag={"x"}
            dragConstraints={{ 
              left: 0, 
              right:  (Math.min( getWindowWidth(), 1920 ) - 400) * 0.92
            }}
            dragElastic={0}
            dragMomentum={false}
            x={position}
          ></Frame>
        </Frame>

      </div>
    );
}
